src/App.tsx.content:
import { Toaster } from "@/components/ui/toaster";
import { Toaster as Sonner } from "@/components/ui/sonner";
import { TooltipProvider } from "@/components/ui/tooltip";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { ThemeProvider } from "@/components/theme-provider";
import { AuthProvider } from "@/contexts/AuthContext";
import ProtectedRoute from "@/components/ProtectedRoute";
import Index from "./pages/Index";
import NotFound from "./pages/NotFound";
import Login from "./pages/auth/Login";
import UserDashboard from "./pages/user/UserDashboard";
import AdminDashboard from "./pages/admin/AdminDashboard";
import ArticleEditor from "./components/cms/ArticleEditor";

const queryClient = new QueryClient();

const App = () => (
  <ThemeProvider defaultTheme="light">
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Sonner />
        <AuthProvider>
          <BrowserRouter>
            <Routes>
              <Route path="/" element={<Index />} />
              <Route path="/login" element={<Login />} />
              <Route 
                path="/dashboard" 
                element={
                  <ProtectedRoute>
                    <UserDashboard />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/admin/*" 
                element={
                  <ProtectedRoute requiredRole={['admin', 'editor']}>
                    <AdminDashboard />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/editor/new" 
                element={
                  <ProtectedRoute requiredRole={['admin', 'editor']}>
                    <ArticleEditor />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/editor/:id" 
                element={
                  <ProtectedRoute requiredRole={['admin', 'editor']}>
                    <ArticleEditor />
                  </ProtectedRoute>
                } 
              />
              <Route path="*" element={<NotFound />} />
            </Routes>
          </BrowserRouter>
        </AuthProvider>
      </TooltipProvider>
    </QueryClientProvider>
  </ThemeProvider>
);

export default App;

src/components/AppLayout.tsx.content:
import React, { useState } from 'react';
import { useAppContext } from '@/contexts/AppContext';
import { useAuth } from '@/contexts/AuthContext';
import { useIsMobile } from '@/hooks/use-mobile';
import Header from './Header';
import NewsCard from './NewsCard';
import { allArticles as articles, Article } from '@/data/articles';
import ArticleModal from './ArticleModal';
import Footer from './Footer';
import StatsSection from './StatsSection';
import { toast } from '@/components/ui/use-toast';

const AppLayout: React.FC = () => {
  const { sidebarOpen, toggleSidebar } = useAppContext();
  const { user, profile, updateProfile } = useAuth();
  const isMobile = useIsMobile();

  const [selectedArticle, setSelectedArticle] = useState<Article | null>(null);
  const [readArticles, setReadArticles] = useState<Set<string>>(new Set());
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('All');

  const handleReadArticle = (articleId: string) => {
    const article = articles.find(a => a.id === articleId);
    if (article) {
      setSelectedArticle(article);
    }
  };

  const handleCompleteReading = async (articleId: string) => {
    if (!user || !profile) {
      toast({
        title: "Please login",
        description: "You need to be logged in to earn tokens",
        variant: "destructive"
      });
      return;
    }

    if (!readArticles.has(articleId)) {
      setReadArticles(new Set([...readArticles, articleId]));

      // Update user tokens
      const newTokens = (profile.tokens || 0) + 50;
      await updateProfile({ tokens: newTokens });

      toast({
        title: "Congratulations! üéâ",
        description: "You earned ‚Ç¶50 for reading this article!",
      });
    }
  };

  const filteredArticles = articles.filter(article => {
    const matchesSearch = article.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         article.excerpt.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || article.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const categories = ['All', 'Politics', 'Technology', 'Sports', 'Business', 'Entertainment'];

  return (
    <div className="min-h-screen bg-gray-50">
      <Header onSearch={setSearchQuery} />

      {/* Hero Section */}
      <section className="relative bg-gradient-to-r from-red-600 to-red-800 text-white">
        <div className="absolute inset-0 bg-black opacity-50"></div>
        <div className="relative container mx-auto px-4 py-16">
          <div className="max-w-4xl">
            <h1 className="text-3xl sm:text-4xl lg:text-5xl font-bold mb-4">
              Stay Informed, Get Rewarded
            </h1>
            <p className="text-lg sm:text-xl mb-8">
              Read quality news and earn tokens for every article you complete. 
              Join thousands of readers earning while staying informed.
            </p>
            <div className="flex flex-col sm:flex-row gap-4">
              {user ? (
                <div className="flex flex-col sm:flex-row items-start sm:items-center gap-4">
                  <span className="text-white text-base sm:text-lg">Welcome back, {profile?.username}!</span>
                  <div className="bg-white text-red-600 px-4 sm:px-6 py-2 sm:py-3 rounded-lg font-semibold text-sm sm:text-base">
                    Balance: ‚Ç¶{profile?.tokens || 0}
                  </div>
                </div>
              ) : (
                <a 
                  href="/login"
                  className="bg-white text-red-600 px-6 sm:px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors inline-block text-center text-sm sm:text-base"
                >
                  Start Reading & Earning
                </a>
              )}
              <button className="border-2 border-white text-white px-8 py-3 rounded-lg font-semibold hover:bg-white hover:text-red-600 transition-colors">
                Learn More
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Category Filter */}
      <div className="container mx-auto px-4 py-6">
        <div className="flex flex-wrap gap-2">
          {categories.map(category => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 rounded-full font-medium transition-colors ${
                selectedCategory === category
                  ? 'bg-red-600 text-white'
                  : 'bg-white text-gray-700 hover:bg-gray-100'
              }`}
            >
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* Articles Grid */}
      <div className="container mx-auto px-4 pb-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredArticles.map(article => (
            <NewsCard
              key={article.id}
              {...article}
              onRead={handleReadArticle}
            />
          ))}
        </div>
      </div>

      <StatsSection />

      <ArticleModal
        article={selectedArticle}
        isOpen={!!selectedArticle}
        onClose={() => setSelectedArticle(null)}
        onComplete={handleCompleteReading}
        isLoggedIn={!!user}
      />

      <Footer />
    </div>
  );
};

export default AppLayout;

src/components/ArticleModal.tsx.content:
import React, { useState, useEffect } from 'react';
import { X, Clock } from 'lucide-react';

interface Article {
  id: string;
  title: string;
  content: string;
  image: string;
  category: string;
  author: string;
  publishedAt: string;
  readTime: number;
}

interface ArticleModalProps {
  article: Article | null;
  isOpen: boolean;
  onClose: () => void;
  onComplete: (articleId: string) => void;
  isLoggedIn: boolean;
}

const ArticleModal: React.FC<ArticleModalProps> = ({ 
  article, isOpen, onClose, onComplete, isLoggedIn 
}) => {
  const [readProgress, setReadProgress] = useState(0);
  const [startTime, setStartTime] = useState<number | null>(null);

  useEffect(() => {
    if (isOpen && article) {
      setStartTime(Date.now());
      setReadProgress(0);
    }
  }, [isOpen, article]);

  useEffect(() => {
    if (!isOpen || !startTime) return;

    const interval = setInterval(() => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min((elapsed / (article!.readTime * 60 * 1000)) * 100, 100);
      setReadProgress(progress);

      if (progress >= 100 && isLoggedIn) {
        onComplete(article!.id);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [startTime, isOpen, article, onComplete, isLoggedIn]);

  if (!isOpen || !article) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg w-full max-w-4xl max-h-[90vh] overflow-hidden">
        <div className="flex justify-between items-center p-4 border-b">
          <div className="flex items-center space-x-4">
            <span className="bg-red-600 text-white px-2 py-1 text-xs font-semibold rounded">
              {article.category}
            </span>
            <div className="flex items-center text-sm text-gray-500">
              <Clock className="w-4 h-4 mr-1" />
              {article.readTime} min read
            </div>
          </div>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X className="w-6 h-6" />
          </button>
        </div>

        {isLoggedIn && (
          <div className="bg-gray-100 p-2">
            <div className="flex justify-between items-center text-sm">
              <span>Reading Progress</span>
              <span>{Math.round(readProgress)}%</span>
            </div>
            <div className="w-full bg-gray-300 rounded-full h-2 mt-1">
              <div 
                className="bg-green-600 h-2 rounded-full transition-all duration-1000"
                style={{ width: `${readProgress}%` }}
              />
            </div>
          </div>
        )}

        <div className="overflow-y-auto max-h-[calc(90vh-120px)]">
          <img 
            src={article.image} 
            alt={article.title}
            className="w-full h-64 object-cover"
          />

          <div className="p-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-4">
              {article.title}
            </h1>

            <div className="flex items-center text-sm text-gray-500 mb-6">
              <span>By {article.author}</span>
              <span className="mx-2">‚Ä¢</span>
              <span>{article.publishedAt}</span>
            </div>

            <div className="prose max-w-none text-gray-700 leading-relaxed">
              {article.content.split('\n').map((paragraph, index) => (
                <p key={index} className="mb-4">
                  {paragraph}
                </p>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ArticleModal;

src/components/Header.tsx.content:
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '@/contexts/AuthContext';
import { Search, Menu, X, User, Bell, LogIn, LogOut, Settings, BarChart, PlusCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { Badge } from '@/components/ui/badge';

interface HeaderProps {
  onSearch?: (query: string) => void;
}

const Header: React.FC<HeaderProps> = ({ onSearch }) => {
  const { user, profile, signOut, isAdmin, isEditor } = useAuth();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (onSearch) {
      onSearch(searchQuery);
    }
  };

  const handleSignOut = async () => {
    await signOut();
    navigate('/');
  };

  return (
    <header className="bg-white shadow-sm border-b sticky top-0 z-50">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <div className="flex items-center">
            <Link to="/" className="text-2xl font-bold text-red-600 hover:text-red-700 transition-colors">
              dadapay
            </Link>
          </div>

          {/* Search Bar - Desktop */}
          <div className="hidden md:flex flex-1 max-w-md mx-8">
            <form onSubmit={handleSearch} className="w-full">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search articles..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full px-4 py-2 pl-10 pr-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-red-500 focus:border-transparent"
                />
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              </div>
            </form>
          </div>

          {/* User Section */}
          <div className="flex items-center gap-4">
            {user && profile ? (
              <>
                {/* Token Balance */}
                <div className="hidden md:flex items-center gap-2">
                  <span className="text-sm text-gray-600">Balance:</span>
                  <Badge variant="secondary" className="font-semibold text-green-600">
                    ‚Ç¶{profile.tokens || 0}
                  </Badge>
                </div>

                {/* Admin/Editor Actions */}
                {(isAdmin || isEditor) && (
                  <div className="hidden md:flex items-center gap-2">
                    <Link to="/editor/new">
                      <Button variant="outline" size="sm">
                        <PlusCircle className="h-4 w-4 mr-2" />
                        New Article
                      </Button>
                    </Link>
                    {isAdmin && (
                      <Link to="/admin">
                        <Button variant="outline" size="sm">
                          <BarChart className="h-4 w-4 mr-2" />
                          Admin
                        </Button>
                      </Link>
                    )}
                  </div>
                )}

                {/* Notifications */}
                <div className="relative">
                  <Bell className="h-5 w-5 text-gray-600 cursor-pointer hover:text-gray-800" />
                  <div className="absolute -top-1 -right-1 h-3 w-3 bg-red-500 rounded-full"></div>
                </div>

                {/* User Dropdown */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="relative h-8 w-8 rounded-full">
                      <Avatar className="h-8 w-8">
                        <AvatarImage src={profile.avatar_url || undefined} />
                        <AvatarFallback>
                          {profile.username?.charAt(0).toUpperCase()}
                        </AvatarFallback>
                      </Avatar>
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent className="w-56" align="end" forceMount>
                    <div className="flex items-center justify-start gap-2 p-2">
                      <div className="flex flex-col space-y-1 leading-none">
                        <p className="font-medium">{profile.username}</p>
                        <p className="w-[200px] truncate text-sm text-muted-foreground">
                          {user.email}
                        </p>
                        <Badge variant="outline" className="w-fit">
                          {profile.role}
                        </Badge>
                      </div>
                    </div>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem asChild>
                      <Link to="/dashboard" className="cursor-pointer">
                        <User className="mr-2 h-4 w-4" />
                        Dashboard
                      </Link>
                    </DropdownMenuItem>
                    {(isAdmin || isEditor) && (
                      <>
                        <DropdownMenuItem asChild>
                          <Link to="/editor/new" className="cursor-pointer">
                            <PlusCircle className="mr-2 h-4 w-4" />
                            Create Article
                          </Link>
                        </DropdownMenuItem>
                        {isAdmin && (
                          <DropdownMenuItem asChild>
                            <Link to="/admin" className="cursor-pointer">
                              <BarChart className="mr-2 h-4 w-4" />
                              Admin Panel
                            </Link>
                          </DropdownMenuItem>
                        )}
                      </>
                    )}
                    <DropdownMenuItem>
                      <Settings className="mr-2 h-4 w-4" />
                      Settings
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem onClick={handleSignOut}>
                      <LogOut className="mr-2 h-4 w-4" />
                      Sign Out
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </>
            ) : (
              <Link to="/login">
                <Button className="bg-red-600 hover:bg-red-700">
                  <LogIn className="h-4 w-4 mr-2" />
                  Login
                </Button>
              </Link>
            )}

            {/* Mobile Menu Button */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2"
            >
              {isMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden border-t py-4 space-y-4">
            <form onSubmit={handleSearch}>
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search articles..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full px-4 py-2 pl-10 pr-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-red-500 focus:border-transparent"
                />
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              </div>
            </form>

            {user && profile && (
              <>
                <div className="flex items-center gap-2 py-2">
                  <span className="text-sm text-gray-600">Balance:</span>
                  <Badge variant="secondary" className="font-semibold text-green-600">
                    ‚Ç¶{profile.tokens || 0}
                  </Badge>
                </div>

                <div className="space-y-2">
                  <Link to="/dashboard" className="block">
                    <Button variant="outline" className="w-full justify-start">
                      <User className="mr-2 h-4 w-4" />
                      Dashboard
                    </Button>
                  </Link>

                  {(isAdmin || isEditor) && (
                    <>
                      <Link to="/editor/new" className="block">
                        <Button variant="outline" className="w-full justify-start">
                          <PlusCircle className="mr-2 h-4 w-4" />
                          Create Article
                        </Button>
                      </Link>
                      {isAdmin && (
